import React, { useRef, useEffect, useState } from "react";

import {
  ListGroup,
  ListGroupItem,
  Row,
  Col,
  Form,
  FormInput,
  FormGroup,
  FormCheckbox,
  FormSelect,
  Button
} from "shards-react";
import { create as ipfsHttpClient } from 'ipfs-http-client'
// const ipfsClient = require('ipfs-http-client');

// const projectId = '1qmt';
// const projectSecret = 'c920';
// const auth =
//     'Basic ' + Buffer.from(projectId + ':' + projectSecret).toString('base64');

// const client = ipfsClient.create({
//     host: 'ipfs.infura.io',
//     port: 5001,
//     protocol: 'https',
//     headers: {
//         authorization: auth,
//     },
// });

const ipfs = ipfsHttpClient('https://ipfs.infura.io:5001/api/v0')



function CompleteFormExample({transactionInstance, account}){

  const [CategorySelect, setCategorySelect] = useState("")
  const [Filename, setFilename] = useState("")
  const [Filedes, setFiledes] = useState("")
  const [Regsitrant, setRegsitrant] = useState("")
  const [Responsible, setResponsible] = useState("")
  const [ipfsHash, setIpfsHash] = useState("");
  const [Filetype, setFiletype] = useState("");
  const [CheckListType, setCheckListType] = useState("");
  
  const [file, setFile] = useState({})
  const [fileUrl, setFileUrl] = useState('')
  const [loading, setLoading] = useState(false)
  const [uploaded, setUploaded] = useState(false)


  const uploadFile = async (e) => {
    // setLoading(true)
    e.preventDefault()

    try {
      console.log('Error ipfs')
        const added = await ipfs.add(file);
        console.log('Error ipfs')
        console.log(file)
        console.log(added)
        const url = `https://ipfs.infura.io/ipfs/${added.path}`
        console.log(url)
        // setUrl(url)
        setFileUrl(url)
        setUploaded(true)
        setIpfsHash(ipfsHash => added.path)
        setLoading(true)
    } catch (err) {
        console.log('Error uploading the file : ', err)
        setLoading(false)
    }
}

const preUpload = (e) => {
    if (e.target.value !== '') {
        setFile(e.target.files[0])
    } else {
        setFile({})
    }
}

  const sendTransaction = async (e) => {

    let cnt = file.name.length;
    let target = '';
    for(let i = cnt-1; i > 0; i-- ){
      target += file.name[i];
      if(file.name[i] == '.') break;
    }
    let temp = '';
    for(let i = target.length-2; i >= 0; i--){
      temp += target[i];
